                    subMenuContents.add(new SubMenuContent(idSubMenu,3, "Shalat Sunnah Istikharah"));
                        detailContents.add(new DetailContent(idDetailContent, "Pengertian Shalat Sunnah Istikharah", idSubMenu, null,null, null, 1));
